improve_prompt:
You are an expert educational game designer. Take the following JavaScript game code and improve ONLY the visuals and audio. Do not change the game mechanics or math logic. Enhance the visual appeal (colors, animations, backgrounds, characters), avoid overstimulation with sounds and visuals, and add or improve sound effects and background audio. Render entirely inside the existing HTML element with ID game-of-the-day-stage. You may create a <canvas> inside it.Have a game area exactly 720px wide by 480px tall.Be written in plain valid JavaScript only—no HTML, no CSS, no comments, and no Markdown formatting.Output only JavaScript code. No extra explanation or formatting.---
const stage=document.getElementById("game-of-the-day-stage");stage.innerHTML="";const canvas=document.createElement("canvas");canvas.width=720;canvas.height=480;stage.appendChild(canvas);const ctx=canvas.getContext("2d");const W=canvas.width,H=canvas.height;class Sound{constructor(src,volume=0.2){this.audio=new Audio(src);this.audio.volume=volume;}play(){const a=this.audio.cloneNode();a.volume=this.audio.volume;a.play();}}const correctSound=new Sound("https://actions.google.com/sounds/v1/cartoon/cartoon_boing.ogg");const wrongSound=new Sound("https://actions.google.com/sounds/v1/cartoon/cartoon_whip.ogg");const bgMusic=new Sound("https://actions.google.com/sounds/v1/ambiences/forest.ogg",0.05);bgMusic.audio.loop=true;bgMusic.audio.play();function lerp(a,b,t){return a+(b-a)*t;}function dist(x1,y1,x2,y2){return Math.hypot(x2-x1,y2-y1);}function randomInt(min,max){return Math.floor(Math.random()*(max-min+1))+min;}function randomColor(){const hues=["#8ecae6","#219ebc","#ffb703","#fb8500","#3a0ca3","#720026","#ff006e","#8338ec"];return hues[randomInt(0,hues.length-1)];}function drawRoundedRect(ctx,x,y,w,h,r,color){ctx.fillStyle=color;ctx.beginPath();ctx.moveTo(x+r,y);ctx.lineTo(x+w-r,y);ctx.quadraticCurveTo(x+w,y,x+w,y+r);ctx.lineTo(x+w,y+h-r);ctx.quadraticCurveTo(x+w,y+h,x+w-r,y+h);ctx.lineTo(x+r,y+h);ctx.quadraticCurveTo(x,y+h,x,y+h-r);ctx.lineTo(x,y+r);ctx.quadraticCurveTo(x,y,x+r,y);ctx.fill();}function drawText(ctx,text,x,y,size,color,align="center"){ctx.fillStyle=color;ctx.font=`${size}px Comic Sans MS`;ctx.textAlign=align;ctx.fillText(text,x,y);}class Explorer{constructor(){this.x=W/2;this.y=H/2;this.size=36;this.color="#4CAF50";this.speed=3;this.target=null;}draw(){ctx.fillStyle=this.color;ctx.beginPath();ctx.arc(this.x,this.y,this.size/2,0,2*Math.PI);ctx.fill();ctx.fillStyle="#fff";ctx.beginPath();ctx.arc(this.x-6,this.y-5,6,0,2*Math.PI);ctx.fill();ctx.fillStyle="#000";ctx.beginPath();ctx.arc(this.x-6,this.y-5,3,0,2*Math.PI);ctx.fill();ctx.fillStyle="#fff";ctx.beginPath();ctx.arc(this.x+6,this.y-5,6,0,2*Math.PI);ctx.fill();ctx.fillStyle="#000";ctx.beginPath();ctx.arc(this.x+6,this.y-5,3,0,2*Math.PI);ctx.fill();ctx.beginPath();ctx.moveTo(this.x,this.y+10);ctx.lineTo(this.x-10,this.y+22);ctx.lineTo(this.x+10,this.y+22);ctx.closePath();ctx.fillStyle="#277135";ctx.fill();}move(){if(!this.target)return;const dx=this.target.x-this.x;const dy=this.target.y-this.y;const distnc=Math.hypot(dx,dy);if(distnc<1){this.x=this.target.x;this.y=this.target.y;this.target=null;return;}const vx=dx/distnc*this.speed;const vy=dy/distnc*this.speed;this.x+=vx;this.y+=vy;}}class Creature{constructor(x,y){this.x=x;this.y=y;this.size=40;this.baseColor=randomColor();this.highlight=false;this.text="";this.answer=null;this.question=null;}draw(){ctx.fillStyle=this.highlight?"#ffef85":this.baseColor;ctx.beginPath();ctx.ellipse(this.x,this.y,this.size*1.3,this.size,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="#fffcf2";ctx.beginPath();ctx.ellipse(this.x,this.y,this.size,this.size*0.6,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="#000";drawText(ctx,this.text,this.x,this.y+10,22,"#333");ctx.strokeStyle="#7b3e19";ctx.lineWidth=3;ctx.beginPath();ctx.moveTo(this.x-10,this.y+this.size*0.7);ctx.lineTo(this.x-15,this.y+this.size+10);ctx.moveTo(this.x+10,this.y+this.size*0.7);ctx.lineTo(this.x+15,this.y+this.size+10);ctx.stroke();}isClicked(px,py){return dist(px,py,this.x,this.y)<this.size*1.3;}setQuestion(){const a=randomInt(1,10);const b=randomInt(1,10);this.question=`${a} + ${b}`;this.answer=a+b;this.text=this.question;}clear(){this.text="";this.answer=null;this.question=null;this.highlight=false;}class Game{constructor(){this.explorer=new Explorer();this.creatures=[];this.score=0;this.total=0;this.currentCreature=null;this.state="start";this.message="";this.timer=0;this.inputAnswer="";this.cursorBlink=true;this.lastBlink=0;this.inputX=300;this.inputY=420;this.createCreatures();window.addEventListener("click",e=>this.handleClick(e));window.addEventListener("keydown",e=>this.handleKey(e));this.animate= this.animate.bind(this);requestAnimationFrame(this.animate);}createCreatures(){for(let i=0;i<5;i++){const x=randomInt(80,W-80);const y=randomInt(100,H-120);const c=new Creature(x,y);c.setQuestion();this.creatures.push(c);}this.nextCreature();}nextCreature(){for(const c of this.creatures)c.highlight=false;const candidates=this.creatures.filter(c=>c.answer!==null);if(candidates.length===0){this.state="finished";this.message=`Adventure complete! You answered ${this.score} out of ${this.total} correctly.`;return;}const idx=randomInt(0,candidates.length-1);this.currentCreature=candidates[idx];this.currentCreature.highlight=true;this.explorer.target={x:this.currentCreature.x,y:this.currentCreature.y-60};this.inputAnswer="";this.total++;}handleClick(e){if(this.state!=="playing"&&this.state!=="start")return;if(this.state==="start"){this.state="playing";return;}const rect=canvas.getBoundingClientRect();const mx=e.clientX-rect.left;const my=e.clientY-rect.top;if(this.currentCreature && this.currentCreature.isClicked(mx,my)){if(this.inputAnswer.length>0){const ans=parseInt(this.inputAnswer);if(ans===this.currentCreature.answer){correctSound.play();this.score++;this.currentCreature.clear();this.nextCreature();}else{wrongSound.play();this.message="Try again!";this.timer=60;}}}else{this.message="Click on the creature's bubble to answer.";} }handleKey(e){if(this.state!=="playing")return;if(e.key.match(/^[0-9]$/)){if(this.inputAnswer.length<3)this.inputAnswer+=e.key;this.message="";}else if(e.key==="Backspace"){this.inputAnswer=this.inputAnswer.slice(0,-1);}else if(e.key==="Enter"){if(this.inputAnswer.length>0){const ans=parseInt(this.inputAnswer);if(ans===this.currentCreature.answer){correctSound.play();this.score++;this.currentCreature.clear();this.nextCreature();}else{wrongSound.play();this.message="Try again!";this.timer=60;}}}}drawBackground(){ctx.fillStyle="#a0c4ff";ctx.fillRect(0,0,W,H);for(let i=0;i<10;i++){ctx.fillStyle="rgba(255,255,255,0.3)";const cx=50+i*70;const cy=400+10*Math.sin(Date.now()/1000+i);ctx.beginPath();ctx.ellipse(cx,cy,30 +5*Math.cos(Date.now()/1200+i),15,0,0,2*Math.PI);ctx.fill();}for(let y=300;y<H;y+=80){for(let x=0;x<W;x+=160){ctx.fillStyle=["#9bdbb8","#76c893"][((x/160)+(y/80))%2];ctx.beginPath();ctx.moveTo(x,y);ctx.lineTo(x+80,y+40);ctx.lineTo(x,y+80);ctx.closePath();ctx.fill();}}}drawUI(){ctx.fillStyle="#333";ctx.font="24px Comic Sans MS";ctx.textAlign="left";ctx.fillText(`Score: ${this.score} / ${this.total-1}`,20,40);drawText(ctx,"Explorer's Addition Quest",W/2,40,28,"#073b4c");}drawInputBox(){drawRoundedRect(ctx,this.inputX-80,this.inputY-32,160,48,12,"#fefae0");ctx.strokeStyle="#219ebc";ctx.lineWidth=3;ctx.strokeRect(this.inputX-80,this.inputY-32,160,48);ctx.fillStyle="#073b4c";ctx.font="28px Comic Sans MS";ctx.textAlign="center";ctx.fillText(this.inputAnswer||"?",this.inputX,this.inputY+12);if(this.cursorBlink){const tw=ctx.measureText(this.inputAnswer||"?").width;ctx.strokeStyle="#219ebc";ctx.lineWidth=2;ctx.beginPath();ctx.moveTo(this.inputX+tw/2+5,this.inputY-12);ctx.lineTo(this.inputX+tw/2+5,this.inputY+20);ctx.stroke();}}drawMessage(){if(!this.message)return;ctx.fillStyle="#ff616f";ctx.font="20px Comic Sans MS";ctx.textAlign="center";ctx.fillText(this.message,W/2,H-20);}animate(t=0){ctx.clearRect(0,0,W,H);this.drawBackground();for(const c of this.creatures)c.draw();this.explorer.move();this.explorer.draw();this.drawUI();if(this.state==="start"){ctx.fillStyle="#073b4c";ctx.font="28px Comic Sans MS";ctx.textAlign="center";ctx.fillText("Welcome, young explorer!",W/2,H/2-60);ctx.fillText("Help the friendly creatures by solving addition puzzles.",W/2,H/2-20);ctx.fillText("Click anywhere to start your adventure.",W/2,H/2+30);}if(this.state==="playing"){if(this.currentCreature){ctx.fillStyle="#073b4c";ctx.font="24px Comic Sans MS";ctx.textAlign="center";ctx.fillText("Solve: "+this.currentCreature.question,this.inputX,H-60);this.drawInputBox();}this.drawMessage();if(this.timer>0)this.timer--;else this.message="";this.cursorBlink=t-this.lastBlink>500?!this.cursorBlink:this.cursorBlink;if(t-this.lastBlink>500)this.lastBlink=t;}if(this.state==="finished"){ctx.fillStyle="#073b4c";ctx.font="28px Comic Sans MS";ctx.textAlign="center";ctx.fillText(this.message,W/2,H/2);}requestAnimationFrame(this.animate);}}const game=new class extends Game{};
