const stage=document.getElementById("game-of-the-day-stage");stage.innerHTML="";const w=720,h=480;const canvas=document.createElement("canvas");canvas.width=w;canvas.height=h;stage.appendChild(canvas);const ctx=canvas.getContext("2d");const audioCtx=new(AudioContext||webkitAudioContext)();function playTone(freq,dur){const osc=audioCtx.createOscillator();const gain=audioCtx.createGain();osc.connect(gain);gain.connect(audioCtx.destination);osc.type="triangle";osc.frequency.value=freq;gain.gain.setValueAtTime(0.1,audioCtx.currentTime);osc.start();osc.stop(audioCtx.currentTime+dur);osc.onended=()=>gain.disconnect();}const bgColor="#d0f0f8";const grassColor="#7ec850";const treeTrunk="#6b4b0a";const treeLeaves="#3a8e30";const characterColor="#f28f8f";const pathColor="#d9cba3";const sounds={correct:261.6,wrong:150,click:392};const inventory=[];class Explorer{constructor(){this.x=360;this.y=240;this.size=32;this.speed=3;}draw(){ctx.fillStyle=characterColor;ctx.beginPath();ctx.ellipse(this.x,this.y,this.size*0.6,this.size,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="white";ctx.beginPath();ctx.ellipse(this.x-8,this.y-10,6,8,0,0,2*Math.PI);ctx.ellipse(this.x+8,this.y-10,6,8,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="black";ctx.beginPath();ctx.ellipse(this.x-6,this.y-8,2,3,0,0,2*Math.PI);ctx.ellipse(this.x+10,this.y-8,2,3,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="#6b3a3a";ctx.beginPath();ctx.moveTo(this.x,this.y+5);ctx.lineTo(this.x-12,this.y+25);ctx.lineTo(this.x+12,this.y+25);ctx.closePath();ctx.fill();}move(dir){switch(dir){case"left":if(this.x-this.speed>50)this.x-=this.speed;break;case"right":if(this.x+this.speed<w-50)this.x+=this.speed;break;case"up":if(this.y-this.speed>50)this.y-=this.speed;break;case"down":if(this.y+this.speed<h-50)this.y+=this.speed;break;}}}class Tree{constructor(x,y){this.x=x;this.y=y;this.width=30;this.height=70;}draw(){ctx.fillStyle=treeTrunk;ctx.fillRect(this.x-5,this.y+30,10,30);ctx.fillStyle=treeLeaves;ctx.beginPath();ctx.moveTo(this.x,this.y);ctx.lineTo(this.x-20,this.y+50);ctx.lineTo(this.x+20,this.y+50);ctx.closePath();ctx.fill();ctx.beginPath();ctx.moveTo(this.x,this.y-20);ctx.lineTo(this.x-15,this.y+25);ctx.lineTo(this.x+15,this.y+25);ctx.closePath();ctx.fill();}}class Stone{constructor(x,y,answer){this.x=x;this.y=y;this.size=28;this.answer=answer;this.selected=false;}draw(){ctx.fillStyle=this.selected?"#ffff88":"#bfbfbf";ctx.beginPath();ctx.ellipse(this.x,this.y,this.size*1.2,this.size,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="black";ctx.font="20px Comic Sans MS";ctx.textAlign="center";ctx.textBaseline="middle";ctx.fillText(this.answer,this.x,this.y);}wasClicked(mx,my){const dx=mx-this.x;const dy=my-this.y;return dx*dx/(this.size*1.2*this.size*1.2)+dy*dy/(this.size*this.size)<=1;}}class Parrot{constructor(){this.x=100;this.y=100;this.state=0;this.frames=[ "#ff634780","#ff450080","#ff7f5080","#ffa07a80" ];this.soundPlaying=false;}draw(){const c=this.frames[this.state%this.frames.length];ctx.fillStyle=c;ctx.beginPath();ctx.ellipse(this.x,this.y,25,20,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="#004400";ctx.beginPath();ctx.moveTo(this.x,this.y-20);ctx.lineTo(this.x-10,this.y+10);ctx.lineTo(this.x+10,this.y+10);ctx.closePath();ctx.fill();ctx.fillStyle="#222222";ctx.beginPath();ctx.ellipse(this.x+10,this.y-5,6,8,0,0,2*Math.PI);ctx.fill();ctx.fillStyle="white";ctx.beginPath();ctx.ellipse(this.x+7,this.y-6,2,2,0,0,2*Math.PI);ctx.fill();}animate(){this.state++;if(this.state%30===0&&Math.random()<0.3){this.x=50+Math.random()*(w-100);this.y=50+Math.random()*(h-100);playTone(440,0.15);}}}const explorer=new Explorer();const trees=[new Tree(120,320),new Tree(480,350),new Tree(600,280),new Tree(300,380),new Tree(550,180),new Tree(180,220)];const stones=[];let currentSum=0;let targetSum=0;let parrot=new Parrot();function pickTarget(){return 5+Math.floor(Math.random()*15);}function placeStones(){stones.length=0;const answers=[];answers.push(targetSum);while(answers.length<6){let val=3+Math.floor(Math.random()*17);if(!answers.includes(val))answers.push(val);}answers.sort(()=>Math.random()-0.5);for(let i=0;i<answers.length;i++){let baseX=100+ i*100 + (i%2)*20;let baseY=400 + ((i%2)*30-15);stones.push(new Stone(baseX,baseY,answers[i]));}}function drawBackground(){ctx.fillStyle=bgColor;ctx.fillRect(0,0,w,h);ctx.fillStyle=grassColor;ctx.fillRect(0,h-100,w,100);ctx.fillStyle=pathColor;ctx.beginPath();ctx.moveTo(0,h-100);ctx.lineTo(720,h-100);ctx.lineTo(720,h-150);ctx.lineTo(0,h-150);ctx.closePath();ctx.fill();trees.forEach(t=>t.draw());}let questionStage=0;function newQuestion(){targetSum=pickTarget();currentSum=0;placeStones();stones.forEach(s=>s.selected=false);questionStage=0;}function drawText(){ctx.fillStyle="#004400";ctx.font="28px Comic Sans MS";ctx.textAlign="left";ctx.fillText("Help Explorer find total sum of: ",20,40);ctx.fillStyle="#006600";ctx.font="36px Comic Sans MS";ctx.fillText(targetSum,400,40);ctx.fillStyle="#004400";ctx.font="24px Comic Sans MS";ctx.fillText("Tapped stones add to Explorer's backpack. Tap stones to reach total!",20,75);ctx.fillStyle="#885544";ctx.font="28px Comic Sans MS";ctx.fillText("Backpack sum: "+currentSum,20,440);}function drawBackpack(){ctx.fillStyle="#a0522d";ctx.fillRect(580,410,120,60);ctx.fillStyle="#fff";ctx.font="24px Comic Sans MS";ctx.fillText("Backpack",590,445);}function checkAnswer(){if(currentSum===targetSum){playTone(784,0.4);parrot.soundPlaying=true;questionStage=2;}else if(currentSum>targetSum){playTone(120,0.5);questionStage=1;}}canvas.onclick=function(e){if(audioCtx.state!=="running")audioCtx.resume();const rect=canvas.getBoundingClientRect();const mx=e.clientX-rect.left;const my=e.clientY-rect.top;if(questionStage!==2){for(let s of stones)if(s.wasClicked(mx,my)){if(!s.selected){s.selected=true;currentSum+=s.answer;playTone(s.answer*20,0.15);checkAnswer();}else{currentSum-=s.answer;s.selected=false;playTone(150,0.15);}break;}}else{newQuestion();}}}function drawExplorer(){explorer.draw();}function animate(){ctx.clearRect(0,0,w,h);drawBackground();drawExplorer();parrot.draw();drawText();drawBackpack();parrot.animate();requestAnimationFrame(animate);}newQuestion();animate();
