improve_prompt:
You are an expert educational game designer. Take the following JavaScript game code and improve ONLY the visuals and audio. Do not change the game mechanics or math logic. Enhance the visual appeal (colors, animations, backgrounds, characters), avoid overstimulation with sounds and visuals, and add or improve sound effects and background audio. Render entirely inside the existing HTML element with ID game-of-the-day-stage. You may create a <canvas> inside it.Have a game area exactly 720px wide by 480px tall.Be written in plain valid JavaScript only—no HTML, no CSS, no comments, and no Markdown formatting.Output only JavaScript code. No extra explanation or formatting.---
const stage=document.getElementById("game-of-the-day-stage");stage.innerHTML="";const canvas=document.createElement("canvas");canvas.width=720;canvas.height=480;stage.appendChild(canvas);const ctx=canvas.getContext("2d");const bgColor="#cfe6f4";const groundColor="#a1d490";const explorerColor="#f47c7c";const friendColor="#7ccdf4";const textColor="#444";const soundCorrect=new Audio("https://actions.google.com/sounds/v1/cartoon/clang_and_wobble.ogg");const soundWrong=new Audio("https://actions.google.com/sounds/v1/cartoon/cartoon_boing.ogg");const bgMusic=new Audio("https://cdn.pixabay.com/download/audio/2021/12/17/audio_0e16e8bde9.mp3?filename=calm-happy-upbeat-ambient-12642.mp3");bgMusic.loop=true;bgMusic.volume=0.1;bgMusic.play();let explorer={x:360,y:400,w:40,h:60,vy:0,jumping:false};let friend={x:120,y:390,w:40,h:60};let trees=[];let treasures=[];let treasuresFound=0;let currentQuestion=null;let questionActive=false;const gravity=0.8;const jumpForce=-15;function randInt(min,max){return Math.floor(Math.random()*(max-min+1))+min}function generateTrees(){trees=[];for(let i=0;i<10;i++){let x=randInt(50,670);let h=randInt(100,180);trees.push({x:x,y:480-h,w:40,h:h})}}function generateTreasures(){treasures=[];let count=0;while(count<5){let x=randInt(60,660);if(!trees.some(t=>Math.abs(t.x-x)<60)){let number=randInt(1,10);treasures.push({x:x,y:420,w:50,h:50,number:number,collected:false});count++}}}function drawBackground(){ctx.fillStyle=bgColor;ctx.fillRect(0,0,720,480);ctx.fillStyle=groundColor;ctx.fillRect(0,460,720,20);trees.forEach(t=>{ctx.fillStyle="#3a6b35";ctx.fillRect(t.x,t.y,t.w,t.h);ctx.fillStyle="#8fbc8f";ctx.beginPath();ctx.ellipse(t.x+20,t.y,40,60,0,0,Math.PI*2);ctx.fill()})}function drawExplorer(){ctx.fillStyle=explorerColor;ctx.fillRect(explorer.x,explorer.y,explorer.w,explorer.h);ctx.fillStyle="#ffddc1";ctx.beginPath();ctx.arc(explorer.x+20,explorer.y+20,15,0,Math.PI*2);ctx.fill();ctx.fillStyle="#fff";ctx.beginPath();ctx.arc(explorer.x+12,explorer.y+18,5,0,Math.PI*2);ctx.arc(explorer.x+28,explorer.y+18,5,0,Math.PI*2);ctx.fill();ctx.fillStyle="#000";ctx.beginPath();ctx.arc(explorer.x+12,explorer.y+18,2,0,Math.PI*2);ctx.arc(explorer.x+28,explorer.y+18,2,0,Math.PI*2);ctx.fill();ctx.strokeStyle="#f47c7c";ctx.beginPath();ctx.moveTo(explorer.x+5,explorer.y+50);ctx.lineTo(explorer.x+35,explorer.y+50);ctx.stroke()}function drawFriend(){ctx.fillStyle=friendColor;ctx.fillRect(friend.x,friend.y,friend.w,friend.h);ctx.fillStyle="#ffebcd";ctx.beginPath();ctx.arc(friend.x+20,friend.y+22,12,0,Math.PI*2);ctx.fill();ctx.fillStyle="#fff";ctx.beginPath();ctx.arc(friend.x+14,friend.y+18,4,0,Math.PI*2);ctx.arc(friend.x+26,friend.y+18,4,0,Math.PI*2);ctx.fill();ctx.fillStyle="#000";ctx.beginPath();ctx.arc(friend.x+14,friend.y+18,1.5,0,Math.PI*2);ctx.arc(friend.x+26,friend.y+18,1.5,0,Math.PI*2);ctx.fill();ctx.strokeStyle=friendColor;ctx.beginPath();ctx.moveTo(friend.x+7,friend.y+48);ctx.lineTo(friend.x+33,friend.y+48);ctx.stroke()}function drawTreasures(){treasures.forEach(t=>{if(t.collected)return;ctx.fillStyle="#ffd700";ctx.beginPath();ctx.moveTo(t.x+25,t.y);ctx.lineTo(t.x+32,t.y+18);ctx.lineTo(t.x+50,t.y+18);ctx.lineTo(t.x+36,t.y+29);ctx.lineTo(t.x+41,t.y+47);ctx.lineTo(t.x+25,t.y+36);ctx.lineTo(t.x+9,t.y+47);ctx.lineTo(t.x+14,t.y+29);ctx.lineTo(t.x,t.y+18);ctx.lineTo(t.x+18,t.y+18);ctx.closePath();ctx.fill();ctx.fillStyle="#333";ctx.font="20px Comic Sans MS";ctx.textAlign="center";ctx.fillText(t.number,t.x+25,t.y+32)})}function drawHUD(){ctx.fillStyle=textColor;ctx.font="22px Comic Sans MS";ctx.textAlign="left";ctx.fillText("Treasures found: "+treasuresFound+"/5",20,40);if(questionActive&&currentQuestion){ctx.fillStyle="#222";ctx.font="24px Comic Sans MS";ctx.textAlign="center";ctx.fillText(currentQuestion.text,360,90);ctx.fillText("Choose the right answer to pick the treasure!",360,120)}}function isColliding(a,b){return a.x<a.x+b.w&&a.x+a.w>b.x&&a.y<a.y+b.h&&a.y+a.h>b.y}function checkTreasureCollision(){if(questionActive)return;for(let t of treasures){if(t.collected)continue;if(explorer.x+explorer.w>t.x&&explorer.x<t.x+t.w&&explorer.y+explorer.h>t.y&&explorer.y<t.y+t.h){askQuestion(t);break}}}function askQuestion(t){questionActive=true;currentQuestion={treasure:t,text:`How many trees do you see near your treasure?`,answers:[]};let nearbyTrees=trees.filter(tree=>Math.abs(tree.x-t.x)<100).length;currentQuestion.correct=nearbyTrees;let options=[nearbyTrees];while(options.length<3){let opt=randInt(0,10);if(!options.includes(opt))options.push(opt)}options.sort(()=>Math.random()-0.5);currentQuestion.answers=options;showQuestion()}function showQuestion(){ctx.fillStyle="rgba(255,255,255,0.85)";ctx.fillRect(140,60,440,120);ctx.fillStyle="#222";ctx.font="20px Comic Sans MS";ctx.textAlign="center";ctx.fillText(currentQuestion.text,360,90);for(let i=0;i<currentQuestion.answers.length;i++){let x=220+i*100;let y=130;ctx.fillStyle="#fff";ctx.fillRect(x-30,y-20,60,40);ctx.strokeStyle="#7ccdf4";ctx.lineWidth=3;ctx.strokeRect(x-30,y-20,60,40);ctx.fillStyle="#333";ctx.font="24px Comic Sans MS";ctx.fillText(currentQuestion.answers[i],x,y+5)}}function selectAnswer(mx,my){if(!questionActive)return;for(let i=0;i<currentQuestion.answers.length;i++){let x=220+i*100;let y=130;if(mx>x-30&&mx<x+30&&my>y-20&&my<y+20){if(currentQuestion.answers[i]===currentQuestion.correct){soundCorrect.play();currentQuestion.treasure.collected=true;treasuresFound++;friendSays("Yay! You’re right! Let’s explore more.");}else{soundWrong.play();friendSays("Oops, try again! Count the trees carefully.");}questionActive=false;currentQuestion=null;}}}let friendSayTimer=0;let friendSayText="";function friendSays(text){friendSayText=text;friendSayTimer=180}function drawFriendSpeech(){if(friendSayTimer>0){ctx.fillStyle="rgba(255,255,255,0.8)";ctx.fillRect(friend.x-10,friend.y-40, 160,30);ctx.fillStyle="#333";ctx.font="16px Comic Sans MS";ctx.textAlign="left";ctx.fillText(friendSayText,friend.x,friend.y-20);friendSayTimer--}}function updateExplorer(){explorer.vy+=gravity;explorer.y+=explorer.vy;if(explorer.y>400){explorer.y=400;explorer.vy=0;explorer.jumping=false}if(explorer.x<0)explorer.x=0;if(explorer.x+explorer.w>720)explorer.x=720-explorer.w}let keys={};window.addEventListener("keydown",e=>{keys[e.code]=true;if(e.code==="Space"&&!explorer.jumping){explorer.vy=jumpForce;explorer.jumping=true}});window.addEventListener("keyup",e=>{keys[e.code]=false});canvas.addEventListener("click",e=>{const rect=canvas.getBoundingClientRect();let mx=e.clientX-rect.left;let my=e.clientY-rect.top;selectAnswer(mx,my)});function loop(){if(keys["ArrowLeft"]){explorer.x-=5}if(keys["ArrowRight"]){explorer.x+=5}updateExplorer();checkTreasureCollision();drawBackground();drawTreasures();drawExplorer();drawFriend();drawHUD();if(questionActive&&currentQuestion)showQuestion();drawFriendSpeech();requestAnimationFrame(loop)}generateTrees();generateTreasures();friendSays("Hi! I’m Zazu the Explorer! Find treasures and count trees!");loop();
